// Get all process steps
const processSteps = [
  document.getElementById('process_step_1'),
  document.getElementById('process_step_2'),
  document.getElementById('process_step_3'),
  document.getElementById('process_step_4'),
  document.getElementById('process_step_5'),
  document.getElementById('process_step_6')
];

// Create intersection observer for animation
const observer = new IntersectionObserver((entries) => {
  entries.forEach(entry => {
    // If element is in view
    if (entry.isIntersecting) {
      // Add animation class
      entry.target.style.animationName = 'fadeInUp';

      // Unobserve after animation is triggered
      observer.unobserve(entry.target);
    }
  });
}, { threshold: 0.1 });

// Observe all process steps
processSteps.forEach(step => {
  if (step) {
    observer.observe(step);
  }
});

// Get all sections for animation
const sections = [
  document.getElementById('features_section'),
  document.getElementById('process_section'),
  document.getElementById('pricing_section'),
  document.getElementById('success_section'),
  document.getElementById('contact_section')
];

// Create intersection observer for sections
const sectionObserver = new IntersectionObserver((entries) => {
  entries.forEach(entry => {
    // If section is in view
    if (entry.isIntersecting) {
      // Add fade in animation
      entry.target.style.animation = 'fadeIn 1s ease-in-out';

      // Unobserve after animation is triggered
      sectionObserver.unobserve(entry.target);
    }
  });
}, { threshold: 0.1 });

// Observe all sections
sections.forEach(section => {
  if (section) {
    sectionObserver.observe(section);
  }
});
